import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [movie, setMovie] = useState([]);

  const fetchMovie = async () => {
    // fetch('https://api.themoviedb.org/3/discover/movie?api_key=27bd4dad0754c77391111e35f827bd6a')
    //   .then(response => response.json())
    //   .then(json => setMovie(json.results))
    const URL = `https://api.themoviedb.org/3/discover/movie?api_key=27bd4dad0754c77391111e35f827bd6a`;
    const response = await fetch(URL);
    const data = await response.json();
    return data;
  }
  useEffect(() => {
    fetchMovie()
  }, [])

  const search = async (q) => {
    const URL = `https://api.themoviedb.org/3/search/movie?query=${q}&api_key=27bd4dad0754c77391111e35f827bd6a`;
    const response = await fetch(URL);
    const data = await response.json();

    if (data.results) {
      setMovie(data.results);
    }
  };

  return (
    <div className='container'>
      <form
        action=""
        className="search-bar"
        onSubmit={(e) => {
          e.preventDefault();
          const searchValue = e.target.querySelector('.MovieSearch').value;
          search(searchValue);
        }}
      >
        <input
          placeholder='Enter Film..'
          className='MovieSearch'
        />
        <button className="search-btn" type="submit">
          <span>Search</span>
        </button>
      </form>

      <div className="dropdown">
        <input type="checkbox" id="dropdown" />
        <label for="dropdown" className="dropdown-btn">
          <span>Framework</span>
          <span className="arrow"></span>
        </label>

        <ul className="dropdown-content" role="menu">
          <li>Movie</li>
          <li>Series</li>
          <li>Episode</li>
        </ul>
      </div>
      <div className="card-container">
        {movie.map((val, index) => (
          <div div className="card" key={index} >
            <div className="wrapper">
              <img src={`https://image.tmdb.org/t/p/w500/${val.poster_path}`} alt={`${val.original_title}`} className="cover-image" />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
