import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [movie, setMovie] = useState([])
  useEffect(() => {
    const fetchMovie = async () => {
      const URL = "https://www.omdbapi.com/?s=gen&apikey=b9601493";
      const response = await fetch(URL);
      const data = await response.json();
      console.log(data.Search);
      setMovie(data.Search)
    }
    fetchMovie()
  }, [])
  return (

    <>
      {
        movie.map((val) => {
          return (
            <div className="card">
              <div className="wrapper">
                <img src="{{val.Poster}}" className="cover-image" />
              </div>
              <h4 className='{{val.Title}}'>The Dark Rider</h4>
            </div>
          )
        })
      }
    </>
  );
}

export default App;
