import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const search = (q) => {
    console.log({ q });
  }
  const [movie, setMovie] = useState([])
  useEffect(() => {
    const fetchMovie = async () => {
      const URL = "https://www.omdbapi.com/?s=gen&apikey=b9601493";
      const response = await fetch(URL);
      const data = await response.json();
      setMovie(data.Search)
    }
    fetchMovie()
  }, [])

  return (
    <div className='container'>
      {/* <form action="" class="search-bar">
        <input placeholder='search film..' className='MovieSearch' onChange={({ target }) => search(target.value)} />
        <button class="search-btn" type="submit">
          <span>Search</span>
        </button>
      </form> */}

      <div className="card-container">
        {
          movie.map((val, index) => {
            return (
              <div className="card" key={index}>
                <div className="wrapper">
                  <img src={val.Poster} className="cover-image" />
                </div>
                <h4 className='{val.Title}'>{val.Title}</h4>
              </div>
            )
          })
        }
      </div>
    </div>
  );
}

export default App;
