import React, { useEffect, useState } from 'react';
import './App.css';

function App() {

  const [movie, setMovie] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  const search = async (q) => {
    if (q.trim() === '') {
      useEffect(() => {
        const fetchMovie = async () => {
          const URL = "https://www.omdbapi.com/?s=gen&apikey=b9601493";
          const response = await fetch(URL);
          const data = await response.json();
          console.log(data.Search);
          setMovie(data.Search)
        }
        fetchMovie()
      }, [])
    }

    const URL = `https://www.omdbapi.com/?s=${q}&apikey=b9601493`;
    console.log(URL);
    const response = await fetch(URL);
    const data = await response.json();

    if (data.Search) {
      setSearchResults(data.Search);
    } else {
      setSearchResults([]);
    }
  };

  return (
    <div className='container'>
      <form
        action=""
        className="search-bar"
        onSubmit={(e) => {
          e.preventDefault();
          const searchValue = e.target.querySelector('.MovieSearch').value;
          search(searchValue);
        }}
      >
        <input
          placeholder='search film..'
          className='MovieSearch'
        />
        <button className="search-btn" type="submit">
          <span>Search</span>
        </button>
      </form>

      <div className="card-container">
        {searchResults.map((val, index) => (
          <div className="card" key={index}>
            <div className="wrapper">
              <img src={val.Poster} className="cover-image" alt={val.Title} />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
