import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [movie, setMovie] = useState([])
  useEffect(() => {
    const fetchMovie = async () => {
      const URL = "https://www.omdbapi.com/?s=gen&apikey=b9601493";
      const response = await fetch(URL);
      const data = await response.json()
      setMovie(data.Search)
    }
    fetchMovie()
  })
  return (
    <div className='container'>
      <form action="" class="search-bar">
        <input placeholder='search film..' className='MovieSearch' required />
        <button class="search-btn" type="submit">
          <span>Search</span>
        </button>
      </form>      <div className="card-container">
        <div className="card">
          <div className="wrapper">
            <img src="https://ggayane.github.io/css-experiments/cards/dark_rider-cover.jpg" className="cover-image" />
          </div>
          <h4 className='title'>The Dark Rider</h4>
        </div>
      </div>
    </div>
  );
}

export default App;
